<template>
  <div class="file-tree">
    <a-directory-tree
        multiple
        :tree-data="treeList"
        :field-names="fieldNames"
    >
      <template #title="item">
        <span>{{ item.path }}</span>
        <a-popconfirm
            title="Are you sure delete this task?"
            ok-text="Yes"
            cancel-text="No"
            @confirm="() => removeHandel(item)"
        >
          <a-button v-if="item.type === 1" danger class="tree-node-but" size="small" type="primary">
            <template #icon>
              <DeleteOutlined />
            </template>
          </a-button>
        </a-popconfirm>
        <a-button v-if="item.type === 1" class="tree-node-but" size="small" type="primary" @click="() => viewHandle(item)">
          <template #icon>
            <EyeOutlined />
          </template>
        </a-button>
      </template>
    </a-directory-tree>

    <a-modal
        style="padding: 25px;width: 450px;height: 450px"
        v-model:visible="data.viewImageVisible"
        footer=""
    >
      <div style="margin: 20px">
        <a-image
            :width="300"
            :height="300"
            :src="data.path"
            fallback="data:image/png;base64,"
        />
      </div>
    </a-modal>

  </div>

</template>

<script lang="ts">
import { FileTreeModel } from '@/model/Models'
import { TreeProps } from 'ant-design-vue'
import type { UploadProps } from 'ant-design-vue'
import { DeleteOutlined, EyeOutlined, CloudUploadOutlined, SearchOutlined } from '@ant-design/icons-vue'
import { reactive, ref } from 'vue'
import { removeApi } from '@/api/storage'
export default {
  name: "FileTree",
  components: {
    DeleteOutlined,
    EyeOutlined,
    CloudUploadOutlined,
    SearchOutlined
  },
  props: {
    treeList: {
      type: Array<FileTreeModel>(),
      default: []
    },
    deleteHandle: {
      type: Function,
      default: () => {}
    },
    requestHeader: {
      type: String,
      default: ''
    }
  },

  setup(props: any) {

    const fieldNames: TreeProps['fieldNames'] = {
      children: 'children',
      title: 'path',
      key: 'index'
    }

    const fileList = ref<UploadProps['fileList']>([]);

    const data = reactive({
      viewImageVisible: false,
      path: '',
      formData: {
        filepath: ''
      }
    })

  

    /**
     *  查看图片请求路径
     */
    const getFilePath = (file: FileTreeModel,index: number = 0, path: string = ''): string => {

      if (file.index === index) {

        return path + '/' + file.path
      }

      if (file.children.length > 0) {

        const filePath = path + '/' + file.path
        file.children.forEach( item => {
          path = getFilePath(item,index,filePath)
        })
      }
      return path
    }

    /**
     * 查看图片
     */
    const viewHandle = (file: FileTreeModel) => {

      data.path = getFilePath(props.treeList[0].children[0],file.index,props.requestHeader)
      data.viewImageVisible = true
    }

    /**
     * 删除文件
     * @param file
     */
    const removeHandel = (file: FileTreeModel) => {
      const path = getFilePath(props.treeList[0].children[0],file.index,'')
      removeApi({ path: path }).then( res => {
        console.log(res)
      })
    }

    return {
      data,
      fieldNames,
      fileList,
      viewHandle,
      removeHandel
    }

  }
}
</script>
